import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Table, TableRow, TableCell, TableHead, TableBody, Switch, TableFooter, FormControlLabel, Chip, Paper,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import LoginIcon from '@mui/icons-material/Login';
import LinkIcon from '@mui/icons-material/Link';
import { useCatch, useEffectAsync } from '../reactHelper';
import { formatBoolean, formatTime, formatShortDate } from '../common/util/formatter';
import { useTranslation } from '../common/components/LocalizationProvider';
import PageLayout from '../common/components/PageLayout';
import SettingsMenu from './components/SettingsMenu';
import CollectionFab from './components/CollectionFab';
import CollectionActions from './components/CollectionActions';
import TableShimmer from '../common/components/TableShimmer';
import { useManager } from '../common/util/permissions';
import SearchHeader, { filterByKeyword } from './components/SearchHeader';
import useSettingsStyles from './common/useSettingsStyles';
import fetchOrThrow from '../common/util/fetchOrThrow';

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.mode === 'light' ? '#fafafa' : '#333',
  },
  '&:hover': {
    backgroundColor: theme.palette.mode === 'light' ? '#f0f8ff' : '#424242',
    transform: 'scale(1.005)',
    transition: 'all 0.3s ease-in-out',
    boxShadow: theme.shadows[3],
    borderLeft: `4px solid ${theme.palette.primary.main}`,
  },
  transition: 'all 0.3s ease-in-out',
  borderRadius: '4px',
  margin: '2px 0',
}));


const UsersPage = () => {
  const { classes } = useSettingsStyles();
  const navigate = useNavigate();
  const t = useTranslation();

  const manager = useManager();

  const [timestamp, setTimestamp] = useState(Date.now());
  const [items, setItems] = useState([]);
  const [searchKeyword, setSearchKeyword] = useState('');
  const [loading, setLoading] = useState(false);
  const [temporary, setTemporary] = useState(false);

  const handleLogin = useCatch(async (userId) => {
    await fetchOrThrow(`/api/session/${userId}`);
    window.location.replace('/');
  });

  const actionLogin = {
    key: 'login',
    title: t('loginLogin'),
    icon: <LoginIcon fontSize="small" />,
    handler: handleLogin,
  };

  const actionConnections = {
    key: 'connections',
    title: t('sharedConnections'),
    icon: <LinkIcon fontSize="small" />,
    handler: (userId) => navigate(`/settings/user/${userId}/connections`),
  };

  useEffectAsync(async () => {
    setLoading(true);
    try {
      const response = await fetchOrThrow('/api/users?excludeAttributes=true');
      setItems(await response.json());
    } finally {
      setLoading(false);
    }
  }, [timestamp]);

  return (
    <PageLayout menu={<SettingsMenu />} breadcrumbs={['settingsTitle', 'settingsUsers']}>
      <SearchHeader keyword={searchKeyword} setKeyword={setSearchKeyword} />
      <Table className={classes.table}>
        <StyledTableHead>
          <TableRow>
            <StyledTableCell>{t('sharedName')}</TableCell>
            <StyledTableCell>{t('userEmail')}</TableCell>
            <StyledTableCell>{t('sharedType')}</TableCell>
            <StyledTableCell>{t('sharedDisabled')}</TableCell>
            <StyledTableCell>{t('userExpirationTime')}</TableCell>
            <StyledTableCell className={classes.columnAction} />
          </TableRow>
        </StyledTableHead>
        <TableBody>
          {!loading ? items.filter((u) => temporary || !u.temporary).filter(filterByKeyword(searchKeyword)).map((item) => (
            <StyledTableRow key={item.id}>
              <TableCell sx={{ padding: '12px 16px', verticalAlign: 'middle' }}>{item.name}</TableCell>
              <TableCell sx={{ padding: '12px 16px', verticalAlign: 'middle' }}>{item.email}</TableCell>
              <TableCell sx={{ padding: '12px 16px', verticalAlign: 'middle' }}>
                {item.administrator ? (
                  <Chip
                    label={t('userTypeAdmin')}
                    size="small"
                    sx={{
                      backgroundColor: '#ffebee',
                      color: '#c62828',
                      borderColor: '#ef5350',
                      fontWeight: 'bold',
                      borderRadius: '16px',
                      padding: '2px 8px'
                    }}
                  />
                ) : (item.userLimit && item.userLimit > 0) ? (
                  <Chip
                    label={t('userTypeManager')}
                    size="small"
                    sx={{
                      backgroundColor: '#fff8e1',
                      color: '#f57f17',
                      borderColor: '#ffd54f',
                      fontWeight: 'bold',
                      borderRadius: '16px',
                      padding: '2px 8px'
                    }}
                  />
                ) : (
                  <Chip
                    label={t('userTypeUser')}
                    size="small"
                    sx={{
                      backgroundColor: '#e3f2fd',
                      color: '#1565c0',
                      borderColor: '#64b5f6',
                      fontWeight: 'bold',
                      borderRadius: '16px',
                      padding: '2px 8px'
                    }}
                  />
                )}
              </TableCell>
              <TableCell sx={{ padding: '12px 16px', verticalAlign: 'middle' }}>{formatBoolean(item.disabled, t)}</TableCell>
              <TableCell sx={{ padding: '12px 16px', verticalAlign: 'middle' }}>{formatShortDate(item.expirationTime)}</TableCell>
              <TableCell className={classes.columnAction} padding="none" sx={{ padding: '12px 16px', verticalAlign: 'middle' }}>
                <CollectionActions
                  itemId={item.id}
                  editPath="/settings/user"
                  endpoint="users"
                  setTimestamp={setTimestamp}
                  customActions={manager ? [actionLogin, actionConnections] : [actionConnections]}
                />
              </StyledTableCell>
            </StyledTableRow>
          )) : (<TableShimmer columns={6} endAction />)}
        </TableBody>
        <TableFooter>
          <TableRow>
            <TableCell colSpan={6} align="right">
              <FormControlLabel
                control={(
                  <Switch
                    value={temporary}
                    onChange={(e) => setTemporary(e.target.checked)}
                    size="small"
                  />
                )}
                label={t('userTemporary')}
                labelPlacement="start"
              />
            </TableCell>
          </TableRow>
        </TableFooter>
      </Table>
      <CollectionFab editPath="/settings/user" />
    </PageLayout>
  );
};

export default UsersPage;
